
const products = [
  { name: 'Haitian Coffee', price: 'Haitian Gourde 500', image: 'data:image/jpeg;base64,' },
  { name: 'Djon Djon', price: 'Haitian Gourde 300', image: 'data:image/jpeg;base64,' },
  { name: 'Pistache Griye', price: 'Haitian Gourde 200', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRPd7bWFbymGydwCydvor3Iuf3u-coeZbssZw&s' },
  { name: 'Argile', price: 'Haitian Gourde 150', image: 'data:image/jpeg;base64,' },
  { name: 'Haitian Chocolate', price: 'Haitian Gourde 400', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1XtkEVRMN1TX2DiJjT2EohT3h1W54yKgRug&s' },
  { name: 'Hot Sauce', price: 'Haitian Gourde 250', image: 'data:image/jpeg;base64,' },
  { name: 'Peanut Butter', price: 'Haitian Gourde 350', image: 'data:image/jpeg;base64,' },
  { name: 'Tea', price: 'Haitian Gourde 200', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQg2Fzf9NAPsdjN9XYFbHKnn8WBny_Gu-podQ&s' },
  { name: 'Castor Oil', price: 'Haitian Gourde 500', image: 'data:image/png;base64,' },
  { name: 'Spaghetti', price: 'Haitian Gourde 150', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSrDprDzEqBQTYJqGTy7CLRNjoaVJDbMcjYzw&s' },
  { name: 'soap', price: 'Haitian Gourde 300', image: 'data:image/jpeg;base64,' },
  { name: 'rice', price: 'Haitian Gourde 400', image: 'data:image/jpeg;base64,' }
];

const gallery = document.querySelector('.gallery');

// --------------------
// CREATE "SEE MORE" BUTTON
// --------------------
let seeMoreBtn = document.getElementById('see-more-btn');
if (!seeMoreBtn) {
  seeMoreBtn = document.createElement('button');
  seeMoreBtn.id = 'see-more-btn';
  seeMoreBtn.textContent = 'See More ';
  seeMoreBtn.style.display = 'none';
  seeMoreBtn.className = 'see-more-btn';
  gallery.parentElement.appendChild(seeMoreBtn);
}

// --------------------
// VARIABLES
// --------------------
let currentIndex = 0;
const PRODUCTS_PER_PAGE = 4;

// --------------------
// LOCALSTORAGE INITIALIZATION
// --------------------
if (!localStorage.getItem('productsViewed')) {
  localStorage.setItem('productsViewed', '0');
}

// --------------------
// FUNCTION TO DISPLAY PRODUCTS
// --------------------
function displayProducts() {
  // Chwazi ki pwodwi pou montre
  const endIndex = Math.min(currentIndex + PRODUCTS_PER_PAGE, products.length);

  products.slice(currentIndex, endIndex).forEach(product => {
    const productCard = document.createElement('div');
    productCard.classList.add('product-card');
    productCard.innerHTML = `
      <img src="${product.image}" alt="${product.name}" loading="lazy">
      <h3>${product.name}</h3>
      <p class="price">${product.price}</p>
      <button class="cart-btn">Add to Cart</button>
    `;
    gallery.appendChild(productCard);
  });

  // Mete ajou localStorage ak kantite pwodwi ki w
  const totalViewed = parseInt(localStorage.getItem('productsViewed'));
  localStorage.setItem('productsViewed', totalViewed + (endIndex - currentIndex));

  // Conditional branching pou bouton "See More"
  if (endIndex < products.length) {
    seeMoreBtn.style.display = 'block';
  } else {
    seeMoreBtn.style.display = 'none';
  }
}

// --------------------
// EVENT LISTENER POU "SEE MORE"
// --------------------
seeMoreBtn.addEventListener('click', () => {
  currentIndex += PRODUCTS_PER_PAGE;
  displayProducts();
});

// --------------------
// INITIAL DISPLAY
// --------------------
displayProducts();
